import React from 'react';
import Home from './components/home'; // Import your Home component
import Resume from './components/Resume'; // Import your Resume component

function App() {
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh', // Ensure the parent container takes the full height of the page
      }}
    >
      {/* Main Content */}
      <div style={{ flex: 1 }}>
        <Home />
        <Resume />
        <section
          id="contact"
          style={{
            padding: '30px 20px',
            textAlign: 'center',
            backgroundColor: '#f4f4f9',
            margin: '20px auto',
            maxWidth: '600px',
            borderRadius: '10px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          }}
        >
          <h2 style={{ marginBottom: '15px' }}>Contact</h2>
          <p>
            <a
              href="mailto:yangshuda215@gmail.com"
              style={{
                color: '#007bff',
                textDecoration: 'none',
                fontSize: '16px',
              }}
            >
              yangshuda215@gmail.com
            </a>
          </p>
          <p style={{ marginTop: '0', fontSize: '16px' }}>
            Phone: (860)-308-3304
          </p>
        </section>
      </div>

      {/* Footer */}
      <footer
        style={{
          backgroundColor: '#333',
          color: 'white',
          textAlign: 'center',
          padding: '10px 0',
        }}
      >
        <p>Â© 2025 Shuda. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
